-- Create syntax for VIEW 'balance_record_view'
CREATE ALGORITHM=UNDEFINED DEFINER=`root`@`%` SQL SECURITY DEFINER VIEW `balance_record_view`
AS SELECT
   `recharge_record`.`CONSUMER_ID` AS `CONSUMER_ID`,
   `recharge_record`.`USER_NO` AS `USER_NO`,
   `recharge_record`.`AMOUNT` AS `AMOUNT`,
   `recharge_record`.`CREATE_DATE` AS `CREATE_DATE`,
   `recharge_record`.`CALLBACK_STATUS` AS `CALLBACK_STATUS`
FROM `recharge_record` where (`recharge_record`.`CALLBACK_STATUS` = 1);


